import React, { Component, useState, useEffect } from 'react';
import { Text, View, TextInput, Button, Image, StyleSheet } from 'react-native';
import useStickyState from '../../useStickyState';
import FormField from '../FormField';
import { Card, ListItem, Icon } from 'react-native-elements'
import { formData } from '../formData';
import wishPage from '../MyLearning'

export default function MyLearningForm({ route, navigation }) {
  const [text, setText] = useState('');
  const [work, setWork] = useState('');
  const [partner, setPartner] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [days, setDays] = useState('');

  const myLearning = route.params.myLearning;
  const writeItemToStorage = route.params.writeItemToStorage;

  const handleFormValueChange = (key, value) => {
    if (key == 'work') {
      setWork(value)
    } else if (key == 'partner') {
      setPartner(value)
    } else if (key == 'startTime') {
      setStartTime(value)
    } else if (key == 'endTime') {
      setEndTime(value)
    }  else if (key == 'days') {
      setDays(value)
    }
  }

  const handleSubmit = () => {
    const obj = {Work:work, Partner:partner, StartTime: startTime, EndTime: endTime, Days:days}
    writeItemToStorage(myLearning.concat(obj))
    navigation.goBack()
  }

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

    <FormField
      label='Work'
      formKey='work'
      placeholder='work'
      handleFormValueChange={handleFormValueChange}
    />
    <FormField
      label='Partner'
      formKey='partner'
      placeholder='partner'
      handleFormValueChange={handleFormValueChange}
    />
    <FormField
      label='StartTime'
      formKey='startTime'
      placeholder='startTime'
      handleFormValueChange={handleFormValueChange}
    />
    <FormField
      label='EndTime'
      formKey='endTime'
      placeholder='endTime'
      handleFormValueChange={handleFormValueChange}
    />
    <FormField
      label='Days'
      formKey='days'
      placeholder='days'
      handleFormValueChange={handleFormValueChange}
    />
    <Card containerStyle= {{padding:8}}>
  {

  }
    </Card>
    <Icon raised name = "check" type = "fontisto" color = "black" onPress={handleSubmit} />
    <Text style = {styles.text}> submit </Text>
    <Text></Text>
    <Text></Text>
    <Text></Text>

    </View>
  );
}

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    textAlign: 'center',
  },
});
